angular.module('app.core').controller('HistoryController', function(StorageService, $location) {	var vm = this;	StorageService.getQuerys().then(function(response) {		 vm.querys = response;	});	vm.addQuery = function(query) {		StorageService.addHistory(query).then(function(data) {			StorageService.addLastPage('history').then(function(response) {				$location.path('/');			});		});	};});